$(document).ready(function(){$("#bt-signin").click(function(a){var e=$("#email").val(),o=$("#password",$("#loginform")).val();$.ajax({async:!0,crossDomain:!0,url:"logon",method:"POST",headers:{"Content-Type":"application/x-www-form-urlencoded","cache-control":"no-cache"},data:{email:e,password:o}}).done(function(a){"OK"==a.code?$(location).attr("href","home"):($("#alert-danger").attr("class","alert alert-danger"),$("#message-error").text(a.message))}).fail(function(a){throw a.message})})});